import React from 'react'
import ReactDOM from 'react-dom'

function createElement(type, props, ...children) {
    return {
        type,
        props: {
            ...props,
            children: children.map(child => {
                if(typeof child === 'object'){
                    return child
                }else{
                    return createPrimitiveElement(child)
                }
            }) 
        }
    }
}

const createPrimitiveElement= (child) => {
    return {
        type: 'PRIMITIVE_ELEMENT',
        props: {
            nodeValue: child,
            children: []
        }
    }
}


const render= (element, container) => {
    const dom= element.type === 'PRIMITIVE_ELEMENT' ? document.createTextNode('') : document.createElement(element.type)

    Object
        .keys(element.props)
        .filter(prop => prop !== 'children')
        .forEach((prop) => {
            dom[prop]= element.props[prop]
        })



    container.appendChild(dom)
}

const MyReact= {
    createElement
}


const element= (
    <div id='react_root'>
        <span>Creating My Own React</span>
    </div>
)

const rootContainer= document.getElementById('root')
ReactDOM.render(element, rootContainer)
